package com.springcloud.userintef.repostories.impl;import com.springcloud.userintef.entity.WaresInfoEntity;import com.springcloud.userintef.repostories.WareInfoRepostory;import org.springframework.stereotype.Repository;import javax.persistence.EntityManager;import javax.persistence.PersistenceContext;import javax.persistence.Query;import java.util.List;@Repository("WareInfoRepostory")public class WareInfoDao implements WareInfoRepostory {    @PersistenceContext    private EntityManager entityManager;    @Override    public List<WaresInfoEntity> findAllBySQL(String sqlStr) {        Query query = entityManager.createNativeQuery(sqlStr);        List<WaresInfoEntity> waresInfoEntityList = query.getResultList();        entityManager.close();        return waresInfoEntityList;    }}